insert into Categories (id_categorie,nom,descriptif) values('1','Boissons',' Liquide destiné à la consommation. On trouve des boissons froides ou chaudes, gazeuses, alcoolisées ou non alcoolisées');
insert into Categories (id_categorie,nom,descriptif) values('2','Fruits et légumes','Le fruit est un aliment végétal, à la saveur sucrée, généralement consommé cru. Les plantes potagères sont des plantes, généralement herbacées, utilisées dans l''alimentation humaine et dont la production relève du maraîchage ou du jardin potager.');
insert into Categories (id_categorie,nom,descriptif) values('3','Céréales et féculents','Féculents : à chaque repas, et selon l''appétit. On classe dans la famille des féculents : le pain et tous les produits de panification (biscottes, pain grillé…), les céréales (riz, blé, orge, avoine, seigle…), les légumineuses (lentilles, fèves, pois chiches, haricots secs…)');
insert into Categories (id_categorie,nom,descriptif) values('4','Produits laitiers','Jan Vermeer, La Laitière, Rijksmuseum Amsterdam, 1658–1660. Les produits laitiers, ou laitages, sont les laits ou les transformations alimentaires obtenues grâce aux laits crus. ');
insert into Categories (id_categorie,nom,descriptif) values('5','Viande poisson œuf','Viandes, blanches ou rouges, poissons, fruits de mer, crustacés, abats, oeufs ou charcuteries… Tous ces aliments ont en commun d''être des sources importantes de protéines. Ils sont également riches en fer et nombreux autres minéraux.');
insert into Categories (id_categorie,nom,descriptif) values('6','Corps gras','Un corps gras est une substance composée de molécules ayant des propriétés hydrophobes. Les corps gras sont majoritairement composés de triglycéride qui sont des esters issus d''une molécule de glycérol et de trois acides gras. Les autres composants forment ce que l''on appelle l''« insaponifiable »');
insert into Categories (id_categorie,nom,descriptif) values('7','Sucre','Le sucre est une substance de saveur douce extraite principalement de la canne à sucre et de la betterave sucrière. Le sucre est une molécule de saccharose (glucose + fructose). Il est également possible d''obtenir du sucre à partir d''autres plantes.');